import React, {Component} from 'react';
import api from '../../services/api';

import './styles.css';

export default class Reminder extends Component{
    state = {
        title: "",
        description: "",
        period: "",
        hour: "" 
    }

    createReminder = async e => {
        e.preventDefault();
        const { title, description, period, hour } = this.state;
        if (!title || !description || !period || !hour) {
          this.setState({ error: "Fill all inputs to create a new reminder" });
        } else {
          try {
            await api.post("/new-reminder", { title, description,
            period, hour });
            this.props.history.push("/main");
            window.location.reload(false);
          } catch (err) {
            this.setState({
              error:
                err
            });
          }
        }
      };

    async componentDidMount(){

        const {id} = this.props.match.params;

        const response = await api.get(`reminders/${id}`);

        this.setState({reminder : response.data});
    }

    render(){
        const {reminder} = this.state;

        return (
            <form onSubmit={this.createReminder}>
            <div className="reminder-info">
                <h1>New Reminder</h1>
                <br></br>
                <p><strong>Title: </strong>
                    <input type="text" value={this.state.title} 
                    placeholder="Type the title here..."></input>
                </p>
                <p><strong>Description: </strong>
                    <textarea value={this.state.description} 
                    placeholder="Type a description here..."></textarea>
                </p>
                <p><strong>Period: </strong> 
                    <input type="text" value={this.state.period}
                    placeholder="Daily"></input>
                </p>
                <p><strong>Hour of Day: </strong> 
                    <input type="time" value={this.state.hour}
                    value="22:00"></input>
                </p>
                <button type="submit">Create</button>
            </div>
            </form>
        );
    }
}
