import React, {Component} from 'react';
import api from '../../services/api';

import './styles.css';

export default class Reminder extends Component{
    state = {
        reminder: {}
    }

    async componentDidMount(){

        const {id} = this.props.match.params;

        const response = await api.get(`reminders/${id}`);

        this.setState({reminder : response.data});
    }

    render(){
        const {reminder} = this.state;

        return (
            <div className="reminder-info">
                <h1>New Reminder</h1>
                <br></br>
                <p><strong>Title: </strong>{reminder.title}
                <input type="text" placeholder="Type the title here..."></input>
                </p>
                <p><strong>Description: </strong>
                    <textarea value={this.state.description} 
                    placeholder="Type a description here..."></textarea>
                </p>
                <p><strong>Period: </strong> 
                    <input type="text" placeholder="Daily"></input>
                </p>
                <p><strong>Hour of Day: </strong> 
                    <input type="time" value="22:00"></input>
                </p>

                <button></button>
            </div>
        );
    }
}
